import React, { useState, useEffect } from 'react';
import api from '../api/axios';

function Results() {
  const [proiecte, setProiecte] = useState([]);

  useEffect(() => {
    const fetchProiecte = async () => {
      const res = await api.get('/projects');
      const proiecteCuNote = res.data;

      // Pentru fiecare proiect, cerem media de la backend
      const proiecteComplete = await Promise.all(proiecteCuNote.map(async (p) => {
        try {
          // Luam livrabilul (presupunem primul pentru acest test)
          const livRes = await api.get(`/results/1`); // Aici in mod real folosim ID-ul livrabilului
          return { ...p, media: livRes.data.media_finala || "N/A" };
        } catch {
          return { ...p, media: "În așteptare" };
        }
      }));
      setProiecte(proiecteComplete);
    };
    fetchProiecte();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Clasament Proiecte</h2>
      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#eee' }}>
            <th>Proiect</th>
            <th>Descriere</th>
            <th>Media Finală (fără extreme)</th>
          </tr>
        </thead>
        <tbody>
          {proiecte.map(p => (
            <tr key={p.proiect_id}>
              <td>{p.titlu}</td>
              <td>{p.descriere}</td>
              <td style={{ fontWeight: 'bold', color: 'blue' }}>{p.media}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Results;
