import React, { useState } from 'react';
import api from '../api/axios';
import { useNavigate } from 'react-router-dom';

function NewProject() {
  const [titlu, setTitlu] = useState("");
  const [descriere, setDescriere] = useState("");
  const [videoLink, setVideoLink] = useState("");
  const [studentId, setStudentId] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 1. Cream proiectul
      const proiectRes = await api.post('/projects', {
        titlu,
        descriere,
        creator_id: studentId
      });

      // 2. Cream si primul livrabil pentru acest proiect
      await api.post('/deliverables', {
        proiect_id: proiectRes.data.proiect_id,
        titlu: "Livrabil Initial",
        video_link: videoLink
      });

      alert("Proiect și livrabil create cu succes!");
      navigate('/'); // Ne intoarcem la dashboard
    } catch (err) {
      alert("Eroare la crearea proiectului");
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Adaugă Proiect Nou</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
        <input placeholder="ID-ul tău de student" type="number" onChange={e => setStudentId(e.target.value)} required />
        <input placeholder="Titlu Proiect" onChange={e => setTitlu(e.target.value)} required />
        <textarea placeholder="Descriere" onChange={e => setDescriere(e.target.value)} />
        <input placeholder="Link Video/Server" onChange={e => setVideoLink(e.target.value)} required />
        <button type="submit" style={{ background: 'green', color: 'white', padding: '10px' }}>Lansează Proiectul</button>
      </form>
    </div>
  );
}

export default NewProject;
