import React, { useState, useEffect } from 'react';
import api from '../api/axios';

function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [studentId, setStudentId] = useState(""); // Îl vom introduce manual pentru test
  const [nota, setNota] = useState("");
  const [selectedTask, setSelectedTask] = useState(null);

  // Funcție care încarcă sarcinile de la backend
  const fetchTasks = async () => {
    if (!studentId) return;
    try {
      const res = await api.get(`/my-tasks/${studentId}`);
      setTasks(res.data);
    } catch (err) {
      alert("Eroare la încărcarea sarcinilor. Verifică ID-ul.");
    }
  };

  // Funcție pentru a trimite nota
  const trimiteNota = async (livrabilId) => {
    try {
      await api.post('/grades', {
        student_id: studentId,
        livrabil_id: livrabilId,
        valoare: nota
      });
      alert("Notă trimisă cu succes!");
      setNota("");
      setSelectedTask(null);
    } catch (err) {
      alert("Eroare: " + err.response.data.error);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>Spark Feedback - Dashboard</h1>

      <div style={{ marginBottom: '20px', background: '#f4f4f4', padding: '10px' }}>
        <label>Introdu ID-ul tău de Student (pentru test): </label>
        <input type="number" value={studentId} onChange={(e) => setStudentId(e.target.value)} />
        <button onClick={fetchTasks}>Încarcă Sarcini</button>
      </div>

      <h3>Proiecte unde ești în Juriu:</h3>
      {tasks.length === 0 ? <p>Introdu ID-ul și apasă butonul pentru a vedea sarcinile.</p> : (
        <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
          {tasks.map(t => (
            <div key={t.jury_id} style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px', width: '250px' }}>
              <h4>{t.livrabil.proiect.titlu}</h4>
              <p><strong>Livrabil:</strong> {t.livrabil.titlu}</p>
              <p><strong>Link:</strong> <a href={t.livrabil.video_link} target="_blank">Vezi Proiect</a></p>

              <button onClick={() => setSelectedTask(t.livrabil_id)}>Acordă Notă</button>

              {selectedTask === t.livrabil_id && (
                <div style={{ marginTop: '10px' }}>
                  <input 
                    type="number" 
                    step="0.01" 
                    placeholder="Nota (1-10)" 
                    value={nota} 
                    onChange={(e) => setNota(e.target.value)} 
                  />
                  <button onClick={() => trimiteNota(t.livrabil_id)}>Trimite</button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
